import { Pipe, PipeTransform } from '@angular/core';
import { DomSanitizer } from '@angular/platform-browser';

@Pipe({
  name: 'highlight',
  standalone: true
})
export class HighlightPipe implements PipeTransform {
  constructor(private sanitizer: DomSanitizer) { }

  transform(value: any, args?: any): any {
    if (!args) {
      return value;
    }

    if (!value)
      return value;

    // Convert value to string in case this is a number
    const stringValue = '' + value;

    // Match in a case insensitive maneer
    const re = new RegExp(args, 'gi');
    const match = stringValue.match(re);

    // If there's no match, just return the original value.
    if (!match) {
      return stringValue;
    }

    const v = stringValue.replace(re, "<mark>" + match[0] + "</mark>")
    return this.sanitizer.bypassSecurityTrustHtml(v);
  }

}

import { HttpClient } from '@angular/common/http';
import { Pipe, PipeTransform } from '@angular/core';
import { DomSanitizer, SafeUrl } from '@angular/platform-browser';
import { map, Observable, of } from 'rxjs';

@Pipe({
  name: 'secure',
  standalone: true
})
export class SecurePipe implements PipeTransform {

  constructor(private http: HttpClient, private sanitizer: DomSanitizer) { }

  transform(url: string): Observable<SafeUrl | null> {
    if (!url) return of(null);
    return this.http
      .get(url, { responseType: 'blob' })
      .pipe(map(val => this.sanitizer.bypassSecurityTrustUrl(URL.createObjectURL(val))));
  }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'shorten',
  standalone: true
})
export class ShortenPipe implements PipeTransform {

  transform(value: string, length?: number): string {
    return (value.length > (length || Infinity)) ? value.slice(0, length) + '.' : value;
  }

}

