}

import { Injectable } from '@angular/core';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { DynamicFormModalComponent } from '../containers/dynamic-form-modal/dynamic-form-modal.component';
import { DynamicFormNotificationModalComponent } from '../containers/dynamic-form-notification-modal/dynamic-form-notification-modal.component';
import { DynamicFormTableModalComponent } from '../containers/dynamic-form-table-modal/dynamic-form-table-modal.component';
import { ModalConfig } from '../models/modal-config.interface';
import { NotificationConfig } from '../models/notification-config.interface';

@Injectable({
  providedIn: 'root'
})
export class DynamicFormService {

  constructor(private modalService: NgbModal) { }

  popDynamicFormModal(config: ModalConfig) {
    config.size = config.size || 'lg'; // Default to large modal
    config.backdrop = config.backdrop || 'static'; // Default to static background
    config.windowClass = config.windowClass || '';

    const modalRef = this.modalService.open(DynamicFormModalComponent, { size: config.size, backdrop: config.backdrop, windowClass: config.windowClass });
    modalRef.componentInstance.config = config;
    modalRef.componentInstance.submitted.subscribe(($e: any) => {
      modalRef.close();
      if (config.onSubmit)
        config.onSubmit($e);
    })
    modalRef.componentInstance.dismissed.subscribe(($e: any) => {
      modalRef.close();
      if (config.onDismiss)
        config.onDismiss($e);
    })
  }

  popDynamicFormTableModal(config: ModalConfig) {
    config.size = config.size || 'lg'; // Default to large modal
    config.backdrop = config.backdrop || 'static'; // Default to static background
    config.windowClass = config.windowClass || '';

    const modalRef = this.modalService.open(DynamicFormTableModalComponent, { size: config.size, backdrop: config.backdrop, windowClass: config.windowClass });
    modalRef.componentInstance.config = config;
    modalRef.componentInstance.submitted.subscribe(($e: any) => {
      modalRef.close();
      if (config.onSubmit)
        config.onSubmit($e);
    })
    modalRef.componentInstance.dismissed.subscribe(($e: any) => {
      modalRef.close();
      if (config.onDismiss)
        config.onDismiss($e);
    })
  }

  popNotification(config: NotificationConfig) {
    config.size = config.size || 'lg'; // Default to large modal
    config.backdrop = config.backdrop || 'static'; // Default to static background
    config.windowClass = config.windowClass || '';

    const modalRef = this.modalService.open(DynamicFormNotificationModalComponent, { size: config.size, backdrop: config.backdrop, windowClass: config.windowClass });
    modalRef.componentInstance.config = config;
    modalRef.componentInstance.submitted.subscribe(($e: any) => {
      modalRef.close();
      if (config.onSubmit)
        config.onSubmit($e);
    })
    modalRef.componentInstance.dismissed.subscribe(($e: any) => {
      modalRef.close();
      if (config.onDismiss)
        config.onDismiss($e);
    })
  }

  popModal(component: any, config: any) {
    config.size = config.size || 'lg'; // Default to large modal
    config.backdrop = config.backdrop || 'static'; // Default to static background
    config.windowClass = config.windowClass || '';

    const modalRef = this.modalService.open(component, { size: config.size, backdrop: config.backdrop, windowClass: config.windowClass });
    modalRef.componentInstance.config = config;
    modalRef.componentInstance.submitted.subscribe(($e: any) => {
      modalRef.close();
      if (config.onSubmit)
        config.onSubmit($e);
    })
    modalRef.componentInstance.dismissed.subscribe(($e: any) => {
      modalRef.close();
      if (config.onDismiss)
        config.onDismiss($e);
    })
  }
}

import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { BehaviorSubject, map, Observable, tap } from 'rxjs';
import { environment } from '../../../../environments/environment';
import { BrowserStorageService } from '../../../core/browser-storage/browser-storage.service';
import { memoize } from '../../../shared/memoize/memoize.decorator';
import { Factory } from './models/factory/factory.model';

const httpOptions = {
  headers: new HttpHeaders({ 'Content-Type': 'application/json' })
};

@Injectable({
  providedIn: 'root'
})
export class FactoryService {

  // Resource url
  private factoryUrl = environment.baseUrl + 'factories';

  // The local storage key for preferences
  LOCAL_STORAGE_KEY = 'FAVOURITE-FACTORY';

  // The list of factories
  factories$: BehaviorSubject<Factory[]> = new BehaviorSubject<Factory[]>([]);

  constructor(private http: HttpClient, private browserStorageService: BrowserStorageService) { }

  // Get the list of factories
  getFactories(): Observable<Factory[]> {
    // Retrieve the favourite factory
    const favouriteFactoryId = this.getFavouriteFactoryId();
    return this.sendGetFactoriesRequest().pipe(
      map(factories => factories.map(f => ({ ...f, favourite: favouriteFactoryId === f.id }))),
      tap(factories => { this.factories$.next(factories); })
    );
  }

  // Mark the specified factory as the favourite factory
  markFavourite(factory: Factory) {
    // Retrieve the factory from the current list of factories
    const factories = this.factories$.getValue();
    const fact = factories.find(x => x.id === factory.id);

    // Exit if factory is not to be found
    if (!fact)
      return;

    // Update the favourite factory in local storage
    this.browserStorageService.set(this.LOCAL_STORAGE_KEY, JSON.stringify(fact.id));

    // Update the list of factories
    factories.forEach(x => { x.favourite = false; });
    fact.favourite = true;

    // Emit new list of factories
    this.factories$.next(factories);
  }

  // Clear the favourite factory
  clearFavourite() {
    // Retrieve the factory from the current list of factories
    const factories = this.factories$.getValue();

    // Update the favourite factory in local storage
    this.browserStorageService.set(this.LOCAL_STORAGE_KEY, JSON.stringify(''));

    // Update the list of factories
    factories.forEach(x => { x.favourite = false; });

    // Emit new list of factories
    this.factories$.next(factories);
  }

  // Retrieve the favourite factory from the local storage
  private getFavouriteFactoryId(): string {
    const pref = JSON.parse(this.browserStorageService.get(this.LOCAL_STORAGE_KEY) || '""');
    return pref;
  }

  @memoize({ extractUniqueId: (args) => args?.[0], clearCacheTimeout: environment.memoizeTimeOut })
  private sendGetFactoriesRequest(): Observable<Factory[]> {
    return this.http.get<Factory[]>(this.factoryUrl, httpOptions);
  }
}

import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable, of } from 'rxjs';
import { map, mergeMap, tap } from 'rxjs/operators';
import { VersionPageService } from '../../shared/components/version-page-template/services/version-page.service';
import { FeatureListVersion } from './models/feature-list-version.model';
import { FeatureListVersionComparison } from '../../shared/components/feature-comparer/models/feature-list-version-comparison.model';
import { FeatureFactoryList } from './models/feature-factory-list.model';
import { FeatureRuleSet } from './models/feature-ruleset.model';
import { BaseDocument } from '../../shared/components/version-page-template/models/document.model';
import { constants } from '../../../../shared/constants/constants';

const httpOptions = {
  headers: new HttpHeaders({ 'Content-Type': 'application/json' })
};

@Injectable({
  providedIn: 'root'
})
export class FeatureListService extends VersionPageService<FeatureListVersion, FeatureListVersionComparison> {
  // Factories
  factoryList$: BehaviorSubject<FeatureFactoryList | null> = new BehaviorSubject<FeatureFactoryList | null>(null);

  // The list of rules
  ruleSet$: BehaviorSubject<FeatureRuleSet | null> = new BehaviorSubject<FeatureRuleSet | null>(null);

  constructor(protected override http: HttpClient) {
    super(http, 'product-feature-list')
  }

  clearData() {
    this.version$.next(null);
    this.factoryList$.next(null);
  }

  getFeatureListDocumentByProductId(productId: string): Observable<BaseDocument | null> {
    return this.getDocumentByProductId<BaseDocument>(productId);
  }

  getFeatureListVersion(id: string): Observable<FeatureListVersion | null> {
    return this.getVersion(id);
  }

  getImportableFeatureListVersion(id: string): Observable<FeatureListVersion | null> {
    return this.getImportableVersion(id);
  }

  // Get factories for the feature list version (from feature list version id)
  getFactories(id: string): Observable<FeatureFactoryList | null> {
    // Compute the version Id
    const computedId = this.computeVersionId(id);

    if (!computedId)
      return of(null).pipe(tap(x => { this.factoryList$.next(null); }));

    const url = `${this.versionUrl}/${computedId}/factories`;
    return this.http.get<FeatureFactoryList>(url).pipe(tap(x => {
      this.factoryList$.next(x);
    }));
  }

  // Get ruleset for the screen version
  getRuleSet(id: string): Observable<FeatureRuleSet | null> {
    // Compute the version Id
    const computedId = this.computeVersionId(id);

    if (!computedId)
      return of(null).pipe(tap(x => { this.ruleSet$.next(null); }));

    const url = `${this.versionUrl}/${computedId}/rules`;
    return this.http.get<FeatureRuleSet>(url).pipe(tap(x => {
      this.ruleSet$.next(x);
    }));
  }

  // Add Features
  addFeatures(id: string, features: { name: string, description: string, category: string, dimensionId: string, isNumeric: boolean, isFreeText: boolean, isArray: boolean, rank: string, isAutoGenerated: boolean }[]): Observable<any> {
    const url = `${this.versionUrl}/${id}/features/add-multiple`;
    const payload = { features };

    return this.http.post(url, payload, httpOptions).pipe(
      map((ids: any) => features.map((f, i) => ({ ...f, id: ids[i], options: [], styleRestrictions: [], notes: '', category: { name: f.category.toUpperCase() }, arraySize: { featureId: constants.emptyGuid, size: 0 } }))),
      // Add all features to current feature list version
      tap(feats => {
        const version = this.version$.getValue();
        if (version) {
          version.features = version.features || [];
          version.features.push(...feats)
          this.version$.next(version);
        }
      }),
      // Create default option for numeric features
      mergeMap(feats => {
        const numericFeatures = feats.filter(f => f.isNumeric);
        const numericOptions = numericFeatures.map(f => ({ featureId: f.id, name: 'NUM', description: 'Numeric value', rank: 'aaa', isAutoGenerated: true }));
        const freeTextFeatures = feats.filter(f => f.isFreeText);
        const freeTextOptions = freeTextFeatures.map(f => ({ featureId: f.id, name: 'TXT', description: 'Free Text', rank: 'aaa', isAutoGenerated: true }));
        const optionsToCreate = [...numericOptions, ...freeTextOptions];
        return optionsToCreate.length ? this.addOptions(id, optionsToCreate).pipe(map(() => feats)) : of(feats);
      })
    )
  }

  // Move a feature
  moveFeature(id: string, featureId: string, rank: string): Observable<any> {
    const url = `${this.versionUrl}/${id}/features/${featureId}/move`;
    const payload = { rank };
    return this.http.post(url, payload, httpOptions).pipe(tap(x => {
      const version = this.version$.getValue();
      const feature = version?.features?.find(s => s.id === featureId);
      if (feature) {
        feature.rank = rank;
        this.version$.next(version);
      }
    }));
  }

  // Delete features
  deleteFeatures(id: string, featureIds: string[]): Observable<any> {
    const url = `${this.versionUrl}/${id}/features/remove-multiple`;
    const payload = { ids: featureIds };
    return this.http.post(url, payload, httpOptions).pipe(tap(x => {
      const version = this.version$.getValue();
      if (version)
        version.features = version.features.filter(f => !featureIds.includes(f.id))
      this.version$.next(version);
    }));
  }

  // Update a feature
  updateFeature(id: string, featureId: string, name: string, description: string, notes: string, categoryName: string, arraySizeFeatureId: string, size: number): Observable<any> {
    const url = `${this.versionUrl}/${id}/features/${featureId}/update-properties`;
    const payload = { name, description, notes, categoryName, arraySizeFeatureId, size: isNaN(size) ? 0 : size };
    return this.http.post(url, payload, httpOptions).pipe(tap(x => {
      const version = this.version$.getValue();
      const feature = version?.features?.find(s => s.id === featureId);
      if (feature) {
        if (name?.length)
          feature.name = name;

        if (description?.length)
          feature.description = description;

        if (notes?.length)
          feature.notes = notes;

        if (categoryName?.length)
          feature.category.name = categoryName.toUpperCase();

        if (arraySizeFeatureId !== constants.emptyGuid && feature.arraySize) {
          feature.arraySize.featureId = arraySizeFeatureId;
          feature.arraySize.size = 0;
        }

        if (size > 0 && arraySizeFeatureId === constants.emptyGuid && feature.arraySize) {
          feature.arraySize.size = size;
          feature.arraySize.featureId = constants.emptyGuid;
        }

        this.version$.next(version);
      }
    }));
  }

  // Update style restriction
  updateStyleRestriction(id: string, featureId: string, styleIds: any): Observable<any> {
    const url = `${this.versionUrl}/${id}/features/${featureId}/update-style-restrictions`;
    const payload = { styleIds };
    return this.http.post(url, payload, httpOptions).pipe(tap(x => {
      const version = this.version$.getValue();
      const feature = version?.features?.find(s => s.id === featureId);
      if (feature) {
        feature.styleRestrictions = styleIds;

        this.version$.next(version);
      }
    }));
  }

  // Options ----------
  addOptions(id: string, options: { featureId: string, name: string, description: string, rank: string, isAutoGenerated: boolean }[]): Observable<any> {
    const url = `${this.versionUrl}/${id}/options/add-multiple`;
    const payload = { options };
    return this.http.post(url, payload, httpOptions).pipe(tap((ids: any) => {
      const version = this.version$.getValue();
      if (version) {
        options.forEach((o, i) => {
          const feature = version.features?.find(s => s.id === o.featureId);
          if (feature) {
            feature.options = feature.options || [];
            feature.options.push({ ...o, id: ids[i], isActive: false, releases: [] });
          }
        });
        this.version$.next(version);
      }
    }));
  }

  moveOption(id: string, featureId: string, optionId: string, rank: string): Observable<any> {
    const url = `${this.versionUrl}/${id}/options/${optionId}/move`;
    const payload = { rank };
    return this.http.post(url, payload, httpOptions).pipe(tap(x => {
      const version = this.version$.getValue();
      const feature = version?.features?.find(s => s.id === featureId);
      const option = feature?.options?.find(s => s.id === optionId);
      if (option) {
        option.rank = rank;
        this.version$.next(version);
      }
    }));
  }

  deleteOptions(id: string, options: { featureId: string, optionId: string }[]): Observable<any> {
    const ids = options.map(o => o.optionId);
    const url = `${this.versionUrl}/${id}/options/remove-multiple`;
    const payload = { ids };
    return this.http.post(url, payload, httpOptions).pipe(tap(x => {
      const version = this.version$.getValue();
      if (version) {
        options.forEach(o => {
          const feature = version.features?.find(s => s.id === o.featureId);
          if (feature)
            feature.options = feature.options.filter(x => x.id !== o.optionId);
        });
        this.version$.next(version);
      }
    }));
  }

  updateOption(id: string, featureId: string, optionId: string, name: string, description: string): Observable<any> {
    const url = `${this.versionUrl}/${id}/options/${optionId}/update-properties`;
    const payload = { name, description };
    return this.http.post(url, payload, httpOptions).pipe(tap(x => {
      const version = this.version$.getValue();
      const feature = version?.features?.find(s => s.id === featureId);
      const option = feature?.options?.find(s => s.id === optionId);
      if (option) {
        if (name?.length)
          option.name = name;

        if (description?.length)
          option.description = description;

        this.version$.next(version);
      }
    }));
  }

  activateOptions(id: string, options: { featureId: string, optionId: string }[]): Observable<any> {
    const ids = options.map(o => o.optionId);
    const url = `${this.versionUrl}/${id}/options/activate-multiple`;
    const payload = { ids };
    return this.http.post(url, payload, httpOptions).pipe(tap(x => {
      const version = this.version$.getValue();
      if (version) {
        options.forEach(o => {
          const feature = version.features?.find(s => s.id === o.featureId);
          const option = feature?.options?.find(s => s.id === o.optionId);
          if (option) {
            option.isActive = true;
          }
        });
        this.version$.next(version);
      }
    }));
  }

  deactivateOptions(id: string, options: { featureId: string, optionId: string }[]): Observable<any> {
    const ids = options.map(o => o.optionId);
    const url = `${this.versionUrl}/${id}/options/deactivate-multiple`;
    const payload = { ids };
    return this.http.post(url, payload, httpOptions).pipe(tap(x => {
      const version = this.version$.getValue();
      if (version) {
        options.forEach(o => {
          const feature = version.features?.find(s => s.id === o.featureId);
          const option = feature?.options?.find(s => s.id === o.optionId);
          if (option) {
            option.isActive = false;
            option.releases = [];
          }
        });
        this.version$.next(version);
      }
    }));
  }

  releaseOptionsToFactory(id: string, options: { featureId: string, optionId: string, asSQ: boolean }[], factoryId: string) {
    const url = `${this.versionUrl}/${id}/options/release-multiple`;
    const payload = { options, factoryId };
    return this.http.post(url, payload, httpOptions).pipe(tap(x => {
      const version = this.version$.getValue();
      if (version) {
        options.forEach(o => {
          const feature = version.features?.find(s => s.id === o.featureId);
          const option = feature?.options?.find(s => s.id === o.optionId);
          if (option)
            option.releases.push({ factoryId, asSQ: o.asSQ });
        });
        this.version$.next(version);
      }
    }));
  }

  unreleaseFeatureOptionsFromFactory(id: string, options: { featureId: string, optionId: string }[], factoryId: string): Observable<any> {
    const optionIds = options.map(o => o.optionId);
    const url = `${this.versionUrl}/${id}/options/unrelease-multiple`;
    const payload = { optionIds, factoryId };
    return this.http.post(url, payload, httpOptions).pipe(tap(x => {
      const version = this.version$.getValue();
      if (version) {
        options.forEach(o => {
          const feature = version.features?.find(s => s.id === o.featureId);
          const option = feature?.options?.find(s => s.id === o.optionId);
          if (option)
            option.releases = option.releases.filter(r => r.factoryId !== factoryId);
        });
        this.version$.next(version);
      }
    }));
  }

  updateOptionReleaseSqRules(id: string, featureId: string, optionId: string, factoryId: string, rules: string[][]): Observable<any> {
    const url = `${this.versionUrl}/${id}/options/${optionId}/update-sq-rules`;
    const payload = { factoryId, rules };
    return this.http.post(url, payload, httpOptions).pipe(tap(x => {
      const version = this.version$.getValue();
      const feature = version?.features?.find(s => s.id === featureId);
      const option = feature?.options?.find(s => s.id === optionId);
      if (option) {
        const release = option.releases?.find(r => r.factoryId === factoryId);
        if (release)
          release.sqRules = rules?.length ? { rules: rules } : undefined;
        this.version$.next(version);
      }
    }));
  }

  updateOptionReleaseUnavailableRules(id: string, featureId: string, optionId: string, factoryId: string, rules: string[][]): Observable<any> {
    const url = `${this.versionUrl}/${id}/options/${optionId}/update-unavailable-rules`;
    const payload = { factoryId, rules };
    return this.http.post(url, payload, httpOptions).pipe(tap(x => {
      const version = this.version$.getValue();
      const feature = version?.features?.find(s => s.id === featureId);
      const option = feature?.options?.find(s => s.id === optionId);
      if (option) {
        const release = option.releases?.find(r => r.factoryId === factoryId);
        if (release)
          release.unavailableRules = rules?.length ? { rules: rules } : undefined;
        this.version$.next(version);
      }
    }));
  }

  updateOptionReleaseContractRules(id: string, featureId: string, optionId: string, factoryId: string, rules: string[][]): Observable<any> {
    const url = `${this.versionUrl}/${id}/options/${optionId}/update-contract-rules`;
    const payload = { factoryId, rules };
    return this.http.post(url, payload, httpOptions).pipe(tap(x => {
      const version = this.version$.getValue();
      const feature = version?.features?.find(s => s.id === featureId);
      const option = feature?.options?.find(s => s.id === optionId);
      if (option) {
        const release = option.releases?.find(r => r.factoryId === factoryId);
        if (release)
          release.contractRules = rules?.length ? { rules: rules } : undefined;
        this.version$.next(version);
      }
    }));
  }

  // Add multiple features and options to a feature list version
  addFeaturesAndOptions(id: string,
    features: { name: string, description: string, category: string, dimensionId: string, isNumeric: boolean, isFreeText: boolean, isArray: boolean, rank: string }[],
    options: { featureName: string, name: string, description: string, rank: string }[]): Observable<any> {
    // Url and payload for features
    const featuresUrl = `${this.versionUrl}/${id}/features/add-multiple`;
    const featuresPayload = { features };

    // Url and payload for options
    const optionsUrl = `${this.versionUrl}/${id}/options/add-multiple`;

    return this.http.post(featuresUrl, featuresPayload, httpOptions).pipe(
      map((ids: any) => features.map((f, i) => ({ ...f, id: ids[i], options: [], styleRestrictions: [], notes: '', category: { name: f.category.toUpperCase() }, arraySize: { featureId: constants.emptyGuid, size: 0 }, isAutoGenerated: false }))),
      // Add all features to current feature list version
      tap(feats => {
        const version = this.version$.getValue();
        if (version) {
          version.features = version.features || [];
          version.features.push(...feats);
          this.version$.next(version);
        }
      }),
      // Create default option for numeric features
      mergeMap(feats => {
        // Auto-generated options
        const numericFeatures = feats.filter(f => f.isNumeric);
        const numericOptions = numericFeatures.map(f => ({ featureId: f.id, name: 'NUM', description: 'Numeric value', rank: 'aaa', isAutoGenerated: true, featureName: f.name }));
        const freeTextFeatures = feats.filter(f => f.isFreeText);
        const freeTextOptions = freeTextFeatures.map(f => ({ featureId: f.id, name: 'TXT', description: 'Free Text', rank: 'aaa', isAutoGenerated: true, featureName: f.name }));

        // Update options from input with features ids
        const inputOptions = options.map(o => ({
          ...o,
          featureId: feats.find(y => y.name === o.featureName)?.id,
          isAutoGenerated: false
        }));

        // Merge auto-generated and input options and create
        const optionsToCreate = [...numericOptions, ...freeTextOptions, ...inputOptions];

        if (optionsToCreate.length) {
          return this.http.post(optionsUrl, { options: optionsToCreate }, httpOptions).pipe(
            tap((ids: any) => {
              const version = this.version$.getValue();
              if (version) {
                optionsToCreate.forEach((o, i) => {
                  const feature = version.features?.find(s => s.id === o.featureId);
                  if (feature) {
                    feature.options = feature.options || [];
                    feature.options.push({ ...o, id: ids[i], featureId: feature.id, isActive: false, releases: [] });
                  }
                });
                this.version$.next(version);
              }
            }),
            map(() => feats)
          )
        }
        else return of(feats);
      })
    );
  }

  getAllProductsReleasedFeaturesAndOptions(): Observable<FeatureListVersion[]> {
    return this.http.get<FeatureListVersion[]>(this.versionUrl, httpOptions);
  }
}

import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class LexicalRankService {

  private minRank = 'AAA';
  private maxRank = 'zzz';
  private alphabetSize = 58; // 26 upper case letters [A-Z], 6 special characters ([ \ ] ^ _ `) and  26 lower case letters [a-z]

  getMinRank() { return this.minRank; }
  getMaxRank() { return this.maxRank; }

  // Return a new rank value for an item to be ranked between 'first' and 'second' ranks
  getRankBetween(first: string, second: string): string {
    // Ensure that the first rank is lower than the second rank
    if (second < first)
      throw 'First rank must be lower than second rank';

    // Normalize the sizes of the first and second ranks
    while (first.length != second.length) {
      if (first.length > second.length)
        second += 'A'
      else
        first += 'A'
    }

    // Extract character codes for each characters of the ranks
    const firstCodes = [...first].map(x => x.charCodeAt(0));
    const secondCodes = [...second].map(x => x.charCodeAt(0));

    // Compute difference between first and second
    // The difference is the number of character between first and second
    let difference = 0;

    for (var i = firstCodes.length - 1; i >= 0; i--) {
      let firstCode = firstCodes[i];
      let secondCode = secondCodes[i];

      // Adjust 
      if (secondCode < firstCode) {
        secondCode += this.alphabetSize;
        secondCodes[i - 1] -= 1;
      }

      // Increment difference
      difference += (secondCode - firstCode) * (this.alphabetSize ** (first.length - i - 1))
    }

    // Generate the new rank
    let newRank = '';

    if (difference <= 1) {
      // Simply add the middle character from the alphabet to the first rank
      newRank = first + String.fromCharCode('A'.charCodeAt(0) + Math.floor(this.alphabetSize / 2));
    } else {
      // Add half of the difference to the first rank
      difference = Math.floor(difference / 2);

      // Intialize offet
      let offset = 0;

      for (var i = 0; i < firstCodes.length; i++) {
        const characterDiff = Math.floor(difference / (this.alphabetSize ** i)) % this.alphabetSize;

        let newCharacterCode = first.charCodeAt(first.length - i - 1) + characterDiff + offset;

        // Reset the offset
        offset = 0;

        // Adjust if new character  is greater than 'Z'
        if (newCharacterCode > 'z'.charCodeAt(0)) {
          offset++;
          newCharacterCode -= this.alphabetSize;
        }

        newRank += String.fromCharCode(newCharacterCode);
      }

      // Reverse the rank characters
      newRank = newRank.split('').reverse().join('');
    }

    // Return
    return newRank;
  }

  // Return N new rank values for N items to be ranked between 'first' and 'second' ranks
  // This is not generating an optimized distribution of ranks
  getRanksBetween(first: string, second: string, number: number): string[] {
    const ret: string[] = [];
    let smaller: string = first;
    for (var i = 0; i < number; i++) {
      smaller = this.getRankBetween(smaller, second);
      ret.push(smaller);
    }
    return ret;
  }

  // Return N new rank values for N items to be ranked between 'first' and 'second' ranks
  // This method generates an optimized distribution of ranks
  getRanksBetweenOptimized(first: string, second: string, number: number): string[] {
    // Ensure that the first rank is lower than the second rank
    if (second < first)
      throw 'First rank must be lower than second rank';

    // Cannot request more rank values than the size of the alphabet
    // Split the problem with recursion
    if (number > this.alphabetSize) {
      // Find the middle rank
      const middleRank = this.getRankBetween(first, second);
      // Distribue the number of rank either side of the middle Rank
      const leftCount = Math.floor(number / 2);
      const rightCount = number - leftCount;
      // Compute the rank values for both ranges
      return [...this.getRanksBetweenOptimized(first, middleRank, leftCount), ... this.getRanksBetweenOptimized(middleRank, second, rightCount)];
    }

    // Normalize the sizes of the first and second ranks
    while (first.length != second.length) {
      if (first.length > second.length)
        second += 'A'
      else
        first += 'A'
    }

    // Extract character codes for each characters of the ranks
    const firstCodes = [...first].map(x => x.charCodeAt(0));
    const secondCodes = [...second].map(x => x.charCodeAt(0));

    // Compute difference between first and second
    // The difference is the number of character between first and second
    let difference = 0;

    for (var i = firstCodes.length - 1; i >= 0; i--) {
      let firstCode = firstCodes[i];
      let secondCode = secondCodes[i];

      // Adjust 
      if (secondCode < firstCode) {
        secondCode += this.alphabetSize;
        secondCodes[i - 1] -= 1;
      }

      // Increment difference
      difference += (secondCode - firstCode) * (this.alphabetSize ** (first.length - i - 1))
    }

    // Generate the new ranks
    const newRanks: string[] = [];

    if (difference <= number) {
      // Simply add the middle character from the alphabet to the first rank
      // TODO: Does not use well the available space in between first and second.
      newRanks.push(...Array(number).fill(null).map((x, i) => first + String.fromCharCode('A'.charCodeAt(0) + Math.floor(this.alphabetSize / (number + 1) * (i + 1)))));
    }
    else {
      // Distribute the difference over the number and add to the first rank
      difference = Math.floor(difference / (number + 1));

      for (var k = 0; k < number; k++) {
        // Intialize offet
        let offset = 0;

        // Initialize loop difference
        let diff = difference * (k + 1);

        // Initialize loop new Rank
        let newRank = '';

        for (var i = 0; i < firstCodes.length; i++) {
          const characterDiff = Math.floor(diff / (this.alphabetSize ** i)) % this.alphabetSize;

          let newCharacterCode = first.charCodeAt(first.length - i - 1) + characterDiff + offset;

          // Reset the offset
          offset = 0;

          // Adjust if new character  is greater than 'Z'
          if (newCharacterCode > 'z'.charCodeAt(0)) {
            offset++;
            newCharacterCode -= this.alphabetSize;
          }

          newRank += String.fromCharCode(newCharacterCode);
        }

        // Reverse the rank characters
        newRank = newRank.split('').reverse().join('');

        // Add the response
        newRanks.push(newRank);
      }


    }

    // Return
    return newRanks;
  }

  // Retrieve the max rank in a list of items
  findMaxRank(items: any[], rankProperty?: string): string {
    // Generate a default rankProperty if not provided
    rankProperty = rankProperty || 'rank';

    // Initialize the maxRank at the minimum possible value 
    let maxRank = this.getMinRank();

    // Traverse the list of items and update the max rank if greater than the currently stored maxRank
    for (var i = 0; i < items.length; i++) {
      if (items[i] && items[i][rankProperty] && items[i][rankProperty] > maxRank)
        maxRank = items[i][rankProperty];
    }

    // Return the max rank
    return maxRank;
  }

  // Retrieve the min rank in a list of items
  findMinRank(items: any[], rankProperty?: string): string {
    // Generate a default rankProperty if not provided
    rankProperty = rankProperty || 'rank';

    // Initialize the minRank at the maximum possible value 
    let minRank = this.getMaxRank();

    // Traverse the list of items and update the min rank if smaller than the currently stored minRank
    for (var i = 0; i < items.length; i++) {
      if (items[i] && items[i][rankProperty] && items[i][rankProperty] < minRank)
        minRank = items[i][rankProperty];
    }

    // Return the min rank
    return minRank;
  }

  // Sort an array of objects based on their ranks
  lexicalRankSorter(rankProperty: string = 'rank'): (a: any, b: any) => 1 | -1 | 0 {
    // Create sorter function
    const sorter = (a: any, b: any) => {
      if (!a[rankProperty]) return -1;
      if (!b[rankProperty]) return 1;
      return a[rankProperty] < b[rankProperty] ? -1 : (a[rankProperty] > b[rankProperty] ? 1 : 0);
    }
    return sorter;
  }
}

