import { Directive, EventEmitter, HostListener, Input, OnDestroy, OnInit, Output } from '@angular/core';
import { debounceTime, Subject, Subscription } from 'rxjs';

@Directive({
  selector: '[click.single],[click.double]',
  standalone: true
})
export class DoubleClickDirective implements OnInit, OnDestroy {
  @Input() debounceTime = 300;
  @Output('click.double') doubleClick = new EventEmitter();
  @Output('click.single') singleClick = new EventEmitter();

  private clicksSubject = new Subject<MouseEvent>();
  private subscription!: Subscription;

  constructor() { }

  ngOnInit() {
    this.subscription = this.clicksSubject.pipe(debounceTime(this.debounceTime)).subscribe(event => {
      if (event.type === 'click') {
        this.singleClick.emit(event);
      } else {
        this.doubleClick.emit(event);
      }
    });
  }

  ngOnDestroy() {
    this.subscription.unsubscribe();
  }

  @HostListener('click', ['$event'])
  clickEvent(event: MouseEvent) {
    event.preventDefault();
    event.stopPropagation();
    this.clicksSubject.next(event);
  }
  @HostListener('dblclick', ['$event'])
  doubleClickEvent(event: MouseEvent) {
    event.preventDefault();
    event.stopPropagation();
    this.clicksSubject.next(event);
  }
}

import { ComponentFactoryResolver, ComponentRef, Directive, Input, OnChanges, OnInit, Type, ViewContainerRef } from '@angular/core';
import { FormGroup } from '@angular/forms';
import { FieldConfig } from '../models/field-config.interface';
import { Field } from '../models/field.interface';
import { FormInputComponent } from './form-input/form-input.component';
import { FormNumberComponent } from './form-number/form-number.component';
import { FormMultiSelectComponent } from './form-multi-select/form-multi-select.component';
import { FormMultiSelectListComponent } from './form-multi-select-list/form-multi-select-list.component';
import { FormSelectComponent } from './form-select/form-select.component';
import { FormCheckboxComponent } from './form-checkbox/form-checkbox.component';
import { FormCheckboxListComponent } from './form-checkbox-list/form-checkbox-list.component';
import { FormPasswordComponent } from './form-password/form-password.component';
import { FormFileComponent } from './form-file/form-file.component';
import { FormStaticComponent } from './form-static/form-static.component';
import { FormStaticAreaComponent } from './form-static-area/form-static-area.component';
import { FormDateComponent } from './form-date/form-date.component';
import { FormDateTimeComponent } from './form-date-time/form-date-time.component';
import { FormAreaComponent } from './form-area/form-area.component';
import { FormButtonComponent } from './form-button/form-button.component';
import { FormImagePreviewComponent } from './form-image-preview/form-image-preview.component';


const components: { [type: string]: Type<Field> } = {
  input: FormInputComponent,
  password: FormPasswordComponent,
  number: FormNumberComponent,
  select: FormSelectComponent,
  multiSelect: FormMultiSelectComponent,
  multiSelectList: FormMultiSelectListComponent,
  checkbox: FormCheckboxComponent,
  checkboxList: FormCheckboxListComponent,
  file: FormFileComponent,
  static: FormStaticComponent,
  area: FormAreaComponent,
  staticArea: FormStaticAreaComponent,
  date: FormDateComponent,
  datetime: FormDateTimeComponent,
  button: FormButtonComponent,
  image: FormImagePreviewComponent
}

@Directive({
  selector: '[dynamicField]',
  standalone: true
})
export class DynamicFieldDirective implements Field, OnInit, OnChanges {
  @Input() config!: FieldConfig;

  @Input() group!: FormGroup;

  component!: ComponentRef<Field>;

  constructor(private resolver: ComponentFactoryResolver, private container: ViewContainerRef) { }

  ngOnInit() {
    if (!components[this.config.type]) {
      const supportedTypes = Object.keys(components).join(', ');
      throw new Error(`Trying to use an unsupported type (${this.config.type}). Supported types: ${supportedTypes}`);
    }
    const component = this.resolver.resolveComponentFactory<Field>(components[this.config.type]);
    this.component = this.container.createComponent(component);
    this.component.instance.config = this.config;
    this.component.instance.group = this.group;
  }

  ngOnChanges() {
    this.config.labelSize = this.config.labelSize != undefined ? this.config.labelSize : 2;

    if (this.component) {
      this.component.instance.config = this.config;
      this.component.instance.group = this.group;
    }
  }
}

import { DOCUMENT } from '@angular/common';
import { ChangeDetectorRef, Directive, ElementRef, Inject, Input, Renderer2, TemplateRef } from '@angular/core';
import { NgbPopover } from "@ng-bootstrap/ng-bootstrap";

@Directive({
    selector: "[stickyPopover]",
    standalone: true
})
export class StickyPopoverDirective extends NgbPopover {

    @Input() stickyPopover!: TemplateRef<any>;

    override triggers!: string;

    override container!: string;

    override toggle(): void {
        super.toggle()
    }

    override  isOpen(): boolean {
        return super.isOpen()
    }

    canClosePopover!: boolean;

    constructor(private _elRef: ElementRef, private _render: Renderer2, @Inject(DOCUMENT) _document: any, _changeDetector: ChangeDetectorRef) {
        super();
        this.triggers = "manual"
        this.container = "body"
    }

    override ngOnInit(): void {
        super.ngOnInit();
        this.ngbPopover = this.stickyPopover;

        this._render.listen(this._elRef.nativeElement, "mouseenter", () => {
            this.canClosePopover = true;
            // this.open()
        });

        this._render.listen(this._elRef.nativeElement, "mouseleave", (event: Event) => {
            this.open();
            setTimeout(() => { if (this.canClosePopover) this.close() }, 200)

        })

        this._render.listen(this._elRef.nativeElement, "click", () => {
            this.close();
        })
    }

    override ngOnDestroy(): void {
        super.ngOnDestroy()
    }

    override open() {
        super.open();
        let popover = window.document.querySelector(".popover")
        this._render.listen(popover, "mouseover", () => {
            this.canClosePopover = false;
        });

        this._render.listen(popover, "mouseout", () => {
            this.canClosePopover = true;
            setTimeout(() => { if (this.canClosePopover) this.close() }, 0)
        });
    }

    override close() {
        super.close();
    }

}

