  @Output()
  dismissed: EventEmitter<any> = new EventEmitter<any>();

  ngOnInit() {
    if (this.config.labelSize != null) // Need to catch null or undefined, hence using != and not !==
      this.config.fields.forEach(x => x.labelSize = this.config.labelSize)
