import { CommonModule } from '@angular/common';
import { Component, forwardRef, Input } from '@angular/core';
import { ControlValueAccessor, NG_VALUE_ACCESSOR, ReactiveFormsModule } from '@angular/forms';
import { MultiSelectListItemComponent } from '../multi-select-item/multi-select-item.component';

@Component({
  selector: 'multi-select-list',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, MultiSelectListItemComponent],
  templateUrl: './multi-select-list.component.html',
  styleUrls: ['./multi-select-list.component.css'],
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => MultiSelectListComponent),
      multi: true
    }
  ],
})
export class MultiSelectListComponent implements ControlValueAccessor {
  private onChange!: (m: any) => void;
  private onTouched!: (m: any) => void;

  @Input() options!: { key: any, value: string }[];
  @Input() placeholder!: string;

  groups: { options: { key: any, value: string, selected: boolean }[] }[] = [];

  get value() {
    return this.groups.map(g => g.options.filter(o => o.selected).map(o => o.key));
  }

  writeValue(v: any): void {
    if (v)
      this.groups = v.map((x: any) => ({
        options: this.options.map(o => ({
          ...o,
          selected: !!x?.includes(o.key)
        }))
      }))

    if (this.onChange)
      this.onChange(this.value);
  }

  registerOnChange(fn: any): void {
    this.onChange = fn;
  }

  registerOnTouched(fn: any): void {
    this.onTouched = fn;
  }

  addGroup(): void {
    this.writeValue([...this.value, []])
  }

  updateValue() {
    if (this.onChange)
      this.onChange(this.value);
  }
}

import { CommonModule } from '@angular/common';
import { Component, EventEmitter, Host, Input, OnInit, Output, ChangeDetectorRef } from '@angular/core';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';

@Component({
  selector: 'multi-select-item',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, FormsModule],
  templateUrl: './multi-select-item.component.html',
  styleUrls: ['./multi-select-item.component.css']
})

export class MultiSelectListItemComponent implements OnInit {
  @Input() placeholder!: string;
  @Input() options!: { key: any, value: string, selected: boolean }[];
  @Input() disabled!: boolean;
  @Output() valueUpdated: EventEmitter<string[]> = new EventEmitter<string[]>();
  model: any;

  showDropDown: boolean = false;

  constructor() {
  }

  ngOnInit(): void {
    setTimeout(() => {
      this.writeValue(this.value);
    });
  }

  get value() {
    return this.options.filter(o => o.selected).map(o => o.key);
  }

  writeValue(val: any): void {
    const valueArray = val instanceof Array ? val : [val];
    const textArray = valueArray.map(v => this.options.find(o => o.key === v)?.value);
    this.model = textArray.length <= 2 ? textArray.join(', ') : textArray.slice(0, 2).join(', ') + ' (+' + (textArray.length - 2) + ')';

    this.valueUpdated.emit(this.value);
  }

  addOrRemove(option: { key: any, value: string, selected: boolean }) {
    setTimeout(() => {
      this.writeValue(this.value);
    });
  }
}

